import axios from 'axios';
import React, { useState } from 'react';
import './App.css';
import SearchForm from './SearchForm';
const apiKey = process.env.REACT_APP_API_KEY;
const apiURL = 'https://api.spoonacular.com/recipes';


function App() {
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState(null);

  const handleSearchByIngredients = async (ingredients) => {
    const endpoint = '/findByIngredients';
    const urlToFetch = apiURL + endpoint;
    const params = {
      ingredients,
      number: 5,
      apiKey
    }
    try {
      const response = await axios.get(urlToFetch, { params });
      setRecipes(response.data);
      console.log(recipes);
      setError(null); // Clear any previous errors
    } catch (error) {
      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Error response:', error.response.data);
        console.error('Error status:', error.response.status);
        console.error('Error headers:', error.response.headers);
        setError(`Error: ${error.response.status} - ${error.response.data.message}`);
      } else if (error.request) {
        // The request was made but no response was received
        console.error('Error request:', error.request);
        setError('No response received from the server.');
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error message:', error.message);
        setError('Error in setting up the request.');
      }
    }
  };



  return (
    <div className="App">
      <h1>What's in your firdge?</h1>
      <SearchForm onSearch={handleSearchByIngredients} />
    </div>
  );

}

export default App;
